    select tree, path into vtree, vpath
      from ter_tree where ter_id=:new.parent_id;
    vpath := vpath||', '||:new.caption;
  exception
    when no_data_found then
      vtree := '/';
      vpath := :new.caption;
  end;
  vtree := vtree||:new.code||'/';
  otree := '/';
