 FROM GOODSGROUP a
    JOIN PURCHASEPLAN pp on pp.GROUPCODE=a.CODE
    JOIN PURCHASEPLANDOC ppd on ppd.ID=pp.PurchasePlanDoc_ID
     LEFT JOIN ORG og on og.ID=pp.CMPORG_ID
